import { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';

function ArticleList() {
  const [articleList, setArticleList] = useState([]);
  const [isExpanded, setIsExpanded] = useState(false);
  const handleExpand = () => {setIsExpanded(true);}
  const handleCollapse = () => {setIsExpanded(false);}

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);}
  const [formData, setFormData] = useState({
    title: '',
    body: '',
    published: false
  });


  useEffect(() => {
    async function fetchData() {
      const response = await axios.get("http://localhost:4000/articles", { headers: { Accept: "application/json" } });
      setArticleList(response.data);
    }
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:4000/articles", formData, { headers: { Accept: "application/json" } });
      setArticleList([...articleList, response.data]);
      setFormData({ title: '', body: '', published: false });
    } catch (error) {
      console.log(error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/articles/${id}`, { headers: { Accept: "application/json" } });
      const updatedList = articleList.filter(article => article.id !== id);
      setArticleList(updatedList);
    } catch (error) {
      console.log(error);
    }
  };


  

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCheckboxChange = (e) => {
    setFormData({ ...formData, published: e.target.checked });
  };

  

  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="title" className="form-label">Title</label>
          <input type="text" className="form-control" id="title" name="title" value={formData.title} onChange={handleChange} />
        </div>
        <div className="mb-3">
          <label htmlFor="body" className="form-label">Body</label>
          <textarea className="form-control" id="body" name="body" value={formData.body} onChange={handleChange} />
        </div>
        <div className="mb-3 form-check">
          <input type="checkbox" className="form-check-input" id="published" name="published" checked={formData.published} onChange={handleCheckboxChange} />
          <label className="form-check-label" htmlFor="published">Published</label>
        </div>
        <button type="submit" className="btn btn-primary">Add Article</button>
      </form>
      <hr />
      <div className="row row-cols-1 row-cols-md-3 g-4">
        {articleList.map(article => (
        <div className="col" key={article.id}>
        <div className="card">
          <div className="card-header">
            <h5 className="mb-0">
              <button className="btn btn-link" onClick={toggleExpand} aria-expanded={isExpanded} aria-controls={`articleCollapse_${article.id}`}>
                {article.title}
              </button>
            </h5>
          </div>
          <div id={`articleCollapse_${article.id}`} className={`collapse ${isExpanded ? 'show' : ''}`} onEntered={handleExpand} onExit={handleCollapse}>
            <div className="card-body">
              <p>{article.body}</p>
              <p>{article.published ? 'Published' : 'Not Published'}</p>
              <Button variant="danger" onClick={() => handleDelete(article.id)}>Delete</Button>
              <Button variant="primary" onClick={() => handleEdit(article)}>Edit</Button>
            </div>
          </div>
        </div>
      </div>
        ))}
      </div>
    </div>
  );
}

export default ArticleList;
