import { useState, useEffect } from 'react';
import axios from 'axios';

function ArticleList() {
  const [articleList, setArticleList] = useState([]);
  const [formData, setFormData] = useState({
    title: '',
    body: '',
    published: false
  });



  useEffect(() => {
    async function fetchData() {
      const response = await axios.get("http://localhost:4000/articles", { headers: { Accept: "application/json" } });
      setArticleList(response.data);
    }
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:4000/articles", formData, { headers: { Accept: "application/json" } });
      setArticleList([...articleList, response.data]);
      setFormData({ title: '', body: '', published: false });
    } catch (error) {
      console.log(error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/articles/${id}`, { headers: { Accept: "application/json" } });
      const updatedList = articleList.filter(article => article.id !== id);
      setArticleList(updatedList);
    } catch (error) {
      console.log(error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCheckboxChange = (e) => {
    setFormData({ ...formData, published: e.target.checked });
  };
  function deleteArticle(id)
  {
    fetch('http://localhost:4000/articles/${id}',{method:'DELETE'})
  }
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={formData.title} onChange={handleChange} />
        </label>
        <br />
        <label>
          Body:
          <textarea name="body" value={formData.body} onChange={handleChange} />
        </label>
        <br />
        <label>
          Published:
          <input type="checkbox" name="published" checked={formData.published} onChange={handleCheckboxChange} />
        </label>
        <br />
        <button type="submit">Add Article</button>
      </form>
      <hr />
      {articleList.map(article => (
        <div key={article.id}>
          <h3>{article.title}</h3>
          <p>{article.body}</p>
          <p>{article.published ? 'Published' : 'Not Published'}</p>
          <button onClick={() => handleDelete(article.id)}>Delete</button>
          <td><button onClick={()=>deleteArticle(article.id)}>Delete</button></td>
        </div>
      ))}
    </div>
  );
}

export default ArticleList;
