import { useState, useEffect } from 'react';
import axios from 'axios';

function ArticleList() {
  const [articleList, setArticleList] = useState([]);
  const [formData, setFormData] = useState({
    title: '',
    body: '',
    published: false
  });

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get("http://localhost:4000/articles", { headers: { Accept: "application/json" } });
      setArticleList(response.data);
    }
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:4000/articles", formData, { headers: { Accept: "application/json" } });
      setArticleList([...articleList, response.data]);
      setFormData({ title: '', body: '', published: false });
    } catch (error) {
      console.log(error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/articles/${id}`, { headers: { Accept: "application/json" } });
      const updatedList = articleList.filter(article => article.id !== id);
      setArticleList(updatedList);
    } catch (error) {
      console.log(error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCheckboxChange = (e) => {
    setFormData({ ...formData, published: e.target.checked });
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Title:</label>
              <input type="text" className="form-control" name="title" value={formData.title} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Body:</label>
              <textarea className="form-control" name="body" value={formData.body} onChange={handleChange} />
            </div>
            <div className="form-group form-check">
              <input type="checkbox" className="form-check-input" name="published" checked={formData.published} onChange={handleCheckboxChange} />
              <label className="form-check-label">Published</label>
            </div>
            <button type="submit" className="btn btn-primary">Add Article</button>
          </form>
          <hr />
          {articleList.map(article => (
            <div key={article.id} className="card my-3">
              <div className="card-body">
                <h3 className="card-title">{article.title}</h3>
                <p className="card-text">{article.body}</p>
                <p className="card-text">{article.published ? 'Published' : 'Not Published'}</p>
                <button onClick={() => handleDelete(article.id)} className="btn btn-danger">Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ArticleList;
